package patterns.sliding_window.min_window_subsequence;

import static org.junit.Assert.assertEquals;

import org.junit.Before;
import org.junit.Test;

public class MyTest {
    private Solution[] methods;

    @Before
    public void init() {
        methods = new Solution[] {
            new BruteForce()
        };
    }

    @Test
    public void simpleTestCase() throws Exception {

        String[] S = new String[] {"abcdebdde", "cnhczmccqouqadqtmjjzl"};
        String[] T = new String[] {"bde", "mm"};
        String[] expected = new String[] {"bcde", "mccqouqadqtm"};

        for (Solution method : methods) {
            for (int i=0; i<S.length; i++) {
                assertEquals(expected[i], method.minWindow(S[i], T[i]));
            }
        }

    }

    @Test
    public void bigInputCase() throws Exception {

        String[] S = new String[] {""};
        String[] T = new String[] {"ereswethisxakuwzddblzcgpmkbzwnzdiayqlollxctiseifmnpdhacvotczgajpcklgsnylkseejouzhafykbwlnpppwmocybfg"};

        String[] expected = new String[] {""};

        for (Solution method : methods) {
            for (int i=0; i<S.length; i++) {
                assertEquals(expected[i], method.minWindow(S[i], T[i]));
            }
        }

    }
}
